import cv2import numpy as npimport timeimport PoseModule#cap = cv2.VideoCapture(0)cap = cv2.VideoCapture('exercises/1.mp4')detector = PoseModule.PoseDetector()count = 0left_direction = 0right_direction =0p_time =0while True:    success, img = cap.read()    #img = cv2.imread('exercises/exercise0.jpg')    img = cv2.resize(img, (1080, 720))    img = detector.findPose(img,False)    lmlist= detector.findPosition(img,False)    if len(lmlist)!=0:        left_angle= detector.findAngle(img,11,13,15)        right_angle=detector.findAngle(img, 12, 14, 16)        left_persantage= np.interp(left_angle,(5,175),(0,100))        right_persantage = np.interp(right_angle, (5, 175), (0, 100))        left_bar = np.interp(left_angle, (5, 175), (100, 650))        right_bar = np.interp(right_angle, (5, 175), (100, 650))        if left_angle < 70 and left_direction ==0:            count+=1            left_direction = 1        if left_angle > 120 and left_direction ==1:            left_direction = 0        if right_angle < 10 and right_direction ==0:            count+=1            right_direction = 1        if right_angle > 170 and right_direction ==1:            right_direction = 0        cv2.rectangle(img, (0,450),(250,720), (50,70,230),cv2.FILLED)        cv2.putText(img,str(count), (70,650),cv2.FONT_HERSHEY_PLAIN,15,(255,250,250),25 )        cv2.rectangle(img, (900, int(left_bar)), (950, 850), (0, 255, 255), cv2.FILLED)        cv2.rectangle(img, (1000, int(right_bar)), (950, 850), (250, 255, 0), cv2.FILLED)        cv2.putText(img, f'{int( (100-left_persantage))}%', (860, 100), cv2.FONT_HERSHEY_PLAIN, 3, (80, 255, 240), 4)        cv2.putText(img, f'{int((100 - right_persantage))}%', (960, 100), cv2.FONT_HERSHEY_PLAIN, 3, (200, 210, 80), 4)    cv2.imshow("img", img)    cv2.waitKey(1)