import cv2import mediapipe as mpimport timecap = cv2.VideoCapture(0)mp_draw = mp.solutions.drawing_utilsmpFaceMesh = mp.solutions.face_meshfaceMesh = mpFaceMesh.FaceMesh(max_num_faces=2)drawSpec = mp_draw.DrawingSpec(thickness=1, circle_radius=1)p_time=0while True:    success, img = cap.read()    c_time= time.time()    fps= 1/(c_time - p_time)    p_time= c_time    img = cv2.resize(img,(1080,720))    imgRGB= cv2.cvtColor(img, cv2.COLOR_BGR2RGB)    results = faceMesh.process(imgRGB)    if results.multi_face_landmarks:        for facelms in results.multi_face_landmarks:            mp_draw.draw_landmarks(img,facelms,mpFaceMesh.FACEMESH_CONTOURS,                                   drawSpec,drawSpec)            for id,lm in enumerate(facelms.landmark):                #print(lm)                ih,iw,ic = img.shape                x,y = int(lm.x*iw), int(lm.y*ih)                print(id,x,y)    cv2.putText(img, f'FPS:{int(fps)}', (20,70),cv2.FONT_HERSHEY_PLAIN,                2,(255,0,255),2)    cv2.imshow('face mesh', img)    cv2.waitKey(1)